import React from 'react'

const Header = (props) => {
  return(
  <>
  <h1>{props.name}</h1>
  </>
  )
}

const Part = (props) => {
  return(
    <>
    <p>{props.name} {props.exercises}</p>
    </>
  )
}

const Content = ({parts}) => {
