import React from 'react'
import Note from './components/Note'

const Header = (props) => {
  console.log("WAWAWEWWAA")
  console.log(props.name)
  return(
  <>
  <h1>{props.name}</h1>
  </>
  )
}

const Part = (props) => {
  return(
    <>
    <p>{props.name} {props.exercises}</p>
    </>
  )
}

const Content = ({parts}) => {
  console.log(parts)
  console.log(" luuuuummahiaaaa")
  return (
    <>

    {parts.map(part => 
           <li><Part key={part.id}  name={part.name} exercises={part.exercises} /></li>
        )}

    </>
  )
}

const Total = ({ parts }) =>{

  const anto = parts.reduce((a, v)=> {
      return(a + v.exercises)}, 0)
  return(
  <h3>
  number of exercises: {anto}
  </h3>
  )
}

const Course = ({ course }) => {
  console.log(course)
  console.log(" ahiaaaa")
  return(
    <>
    <ul>
    {course.map(ciao =>
          <div key={ciao.id}>
          <Header name={ciao.name} />
          <Content parts={ciao.parts}/>
          <Total parts={ciao.parts} />
          </div>
        )}
    </ul>
    </>
  )
}

const App = () => {
const courses = [
  {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  }, 
  {
    name: 'Node.js',
    id: 2,
    parts: [
      {
        name: 'Routing',
        exercises: 3,
        id: 1
      },
      {
        name: 'Middlewares',
        exercises: 7,
        id: 2
      }
    ]
  }
]
return (
  <div>
    <Course course = {courses} />
  </div>
)
}

export default App
