import React, { useState, useEffect } from 'react'
import personsService from './services/persons'

const Delete = ({name, number}) => {
  return(
    <>
    <button >remove  </button>
    </>
  )
}  


const Contact = ({name, number}) => {
  return(
    <>
    <li>{name} {number} <button onClick={() => deletePerson(person.id)}>delete</button> </li>
    </>
  )
}  

const App = () => {
  const [persons, setPersons] = useState(
  []) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  useEffect(() => {
    console.log('effect')
    personsService
      .getAll()
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }, [])

  const removePerson = (event, id) => {
    event.preventDefault()
    personsService
    .remove(id)
    .then(response => {
      setPersons(persons.concat(response.data))
    })
  }

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber,
      id: persons.length + 1,
    }
    const currentPerson = persons.filter((person) => person.name === newName);
    if (currentPerson.length === 1)
    {
      alert(
          `${newName} is already added to phonebook, add anyway?`
        )
    }
  personsService
  .create(personObject)
  .then(response => {
    setPersons(persons.concat(response.data))
    setNewName('')
    setNewNumber('')
  })
}

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        name: <input
          value={newName}
          onChange={handleNameChange}
        /> <br></br>
        number: <input
          value={newNumber}
          onChange={handleNumberChange}
        />

      <button type="submit">Add person</button>
      </form>   
      <h2>Numbers</h2>
      <ul>
        {persons.map(contact =>
        <p>
          <Contact key={contact.name} name={contact.name} number = {contact.number} /> 
        </p>
        )}
      </ul>
    </div>
  )
}

export default App
